def assert_existence(path):
	if not exists(path):
		raise BadParameter(f"\"{path}\" does not exist.")

def assert_is_file(path):
	assert_existence(path)
	if isdir(path):
		raise BadParameter(f"{path} is a directory.")

def assert_file_type(file_path, type_extension):
	assert_is_file(file_path)
	if Path(file_path).suffix != type_extension:
		raise BadParameter(f"{file_path} is not a {type_extension.lstrip('.')} file.")

def assert_is_dir(path):
	assert_existence(path)
	if not isdir(path):
		raise BadParameter(f"{path} is not a directory.")

def check_jar_file(ctx, param, file_path):
	assert_file_type(file_path, '.jar')
	return Path(file_path).absolute()

def check_icon_file(ctx, param, file_path):
	if file_path:
		assert_file_type(file_path, '.icns')
		file_path = Path(file_path).absolute()

	return file_path

def check_destination_directory(ctx, param, directory_path):
	if directory_path:
		assert_is_dir(directory_path)
		directory_path = Path(directory_path).absolute()

	return directory_path
